INSERT INTO document_lessons (module_id, title, content, code_example, position) VALUES
(1, 'Tại sao cần thuật toán sắp xếp?', 'Sắp xếp là một trong những operations cơ bản nhất trong computer science...', 'def why_sorting():\n    # Sorting giúp tìm kiếm nhanh hơn\n    return "Better search performance"', 1),
(1, 'Phân loại thuật toán sắp xếp', 'Có nhiều cách phân loại sorting algorithms: stable vs unstable, in-place vs out-of-place...', NULL, 2),
(2, 'Bubble Sort Implementation', 'Bubble Sort so sánh các cặp elements liền kề và swap nếu cần thiết...', 'def bubble_sort(arr):\n    n = len(arr)\n    for i in range(n):\n        for j in range(0, n-i-1):\n            if arr[j] > arr[j+1]:\n                arr[j], arr[j+1] = arr[j+1], arr[j]\n    return arr', 1),
(2, 'Bubble Sort Complexity Analysis', 'Time complexity của Bubble Sort là O(n²) trong worst case...', NULL, 2),
(3, 'Selection Sort Concept', 'Selection Sort tìm minimum element và đưa về đầu array...', 'def selection_sort(arr):\n    for i in range(len(arr)):\n        min_idx = i\n        for j in range(i+1, len(arr)):\n            if arr[min_idx] > arr[j]:\n                min_idx = j\n        arr[i], arr[min_idx] = arr[min_idx], arr[i]\n    return arr', 1),
(4, 'What are Classes?', 'Classes là blueprints để tạo objects. Chúng define attributes và methods...', 'class Student:\n    def __init__(self, name, age):\n        self.name = name\n        self.age = age\n    \n    def study(self):\n        return f"{self.name} is studying"', 1),
(4, 'Objects và Instances', 'Objects là instances của classes. Mỗi object có state riêng...', 'student1 = Student("An", 20)\nstudent2 = Student("Bình", 21)\nprint(student1.study())', 2),
(5, 'Inheritance in Python', 'Inheritance cho phép class con inherit attributes và methods từ class cha...', 'class Person:\n    def __init__(self, name):\n        self.name = name\n\nclass Student(Person):\n    def __init__(self, name, student_id):\n        super().__init__(name)\n        self.student_id = student_id', 1),
(7, 'SQL SELECT Statement', 'SELECT là câu lệnh cơ bản nhất để query data từ database...', 'SELECT name, age FROM users\nWHERE age > 18\nORDER BY name;', 1),
(7, 'Filtering với WHERE', 'WHERE clause cho phép filter records dựa trên conditions...', 'SELECT * FROM products\nWHERE price BETWEEN 100 AND 500\nAND category = "Electronics";', 2),
(8, 'INNER JOIN Explained', 'INNER JOIN return records có matching values trong cả hai tables...', 'SELECT u.name, p.title\nFROM users u\nINNER JOIN posts p ON u.id = p.user_id;', 1),
(8, 'LEFT JOIN vs RIGHT JOIN', 'LEFT JOIN return tất cả records từ left table...', 'SELECT u.name, p.title\nFROM users u\nLEFT JOIN posts p ON u.id = p.user_id;', 2),
(10, 'useState Hook Basics', 'useState cho phép add state vào functional components...', 'import React, { useState } from "react";\n\nfunction Counter() {\n  const [count, setCount] = useState(0);\n  \n  return (\n    <button onClick={() => setCount(count + 1)}>\n      Count: {count}\n    </button>\n  );\n}', 1),
(10, 'State Updates và Re-rendering', 'Khi state thay đổi, component sẽ re-render với giá trị mới...', 'const [user, setUser] = useState({ name: "", age: 0 });\n\n// Correct way to update object state\nsetUser(prevUser => ({\n  ...prevUser,\n  name: "John"\n}));', 2),
(11, 'useEffect Hook Introduction', 'useEffect cho phép perform side effects trong functional components...', 'import React, { useState, useEffect } from "react";\n\nfunction UserProfile({ userId }) {\n  const [user, setUser] = useState(null);\n  \n  useEffect(() => {\n    fetchUser(userId).then(setUser);\n  }, [userId]);\n  \n  return user ? <div>{user.name}</div> : <div>Loading...</div>;\n}', 1),
(13, 'Express Server Setup', 'Express là minimal web framework cho Node.js...', 'const express = require("express");\nconst app = express();\n\napp.get("/", (req, res) => {\n  res.json({ message: "Hello World" });\n});\n\napp.listen(3000, () => {\n  console.log("Server running on port 3000");\n});', 1),
